create table email_sending_request (id bigint not null auto_increment, batch_delay bigint not null, batch_size integer not null, campaign_id varchar(255) not null, customer_exclusion_list_id varchar(255), customer_id varchar(255) not null, customer_template_id varchar(255) not null, email_data_file varchar(255), from_email varchar(255) not null, reply_to varchar(255), schedule tinyblob, sender_token varchar(255) not null, subject varchar(255) not null, primary key (id));
create table email_template (id bigint not null auto_increment, category varchar(255), css_subpath varchar(255), customer_id varchar(255), customer_template_id varchar(255), description varchar(255), friendly_name varchar(255), html_filename varchar(255), images_subpath varchar(255), template_subdir varchar(255), txt_filename varchar(255), uploaded_zip_filename varchar(255), primary key (id));
create table exclusion_list_entry (id bigint not null auto_increment, customer_id varchar(255) not null, customer_list_id varchar(255) not null, description varchar(255), reason integer not null, recipient_email varchar(255) not null, primary key (id));
create table user_info (id bigint not null auto_increment, customer_id varchar(255), first_name varchar(255), last_name varchar(255), password varchar(255), provider_subaccount_id varchar(255), provider_token varchar(255), sender_token varchar(255), username varchar(255), primary key (id));
